// simple wrapper around JSON load/dump
import yaml from 'js-yaml';

/**
 * Function to encode YAML
 * @param {Object} data - The data to encode as YAML text
 * @return {String} a YAML encoded string
 * @example
 * encode({ message: 'Hello World' })
 */
const encode = data => yaml.dump(data);

/**
 * Function to decode YAML
 * @param {String} text - The YAML text to decode
 * @return {Object|Array} the decoded object or array
 * @example
 * decode("message: Hello World")
 */
const decode = text => yaml.load(text);

/**
 * An object containing the YAML `encode` and `decode` functions
 */
export const yamlCodec = { encode, decode };

export default yamlCodec

import json from './Json.js'
import yaml from './Yaml.js'

/**
 * Codecs provide a consistent encode()/decode() interface for serialising
 * and de-serialising data.  This standard naming convention makes it possible
 * for the ../Filesystem/File.js module to support a "codec" option for
 * files. When this option is set the file.read() and file.write() methods
 * automatically handle the translation to and from the serialised format
 * using a codec object returned by the codec() function below.  The codec
 * name can be specified in any case, e.g. "Yaml", "YAML", "yaml", "YaML",
 * etc., and it will be converted to lower case.
 */

/**
 * Lookup table for codecs
 */
export const codecs = {
  json, yaml
};

/**
 * Function to fetch a codec
 * @param {string} name - The title of the code, in any case, e.g. "yaml", "YAML", "Yaml"
 */
export const codec = name => codecs[
  name.toLowerCase()
];

export default codecs

import { addDebug } from "./Utils/Debug.js";

export class Component {
  constructor(workspace, props={}) {
    this.workspace = workspace;
    this.props = props;
    addDebug(this, props.debug, props.debugPrefix, props.debugColor);
    this.initComponent(props);
  }
  initComponent() {
    // stub for subclasses
  }
}

export default Component

import { DirPath } from './Filesystem/DirPath.js';
import { addDebug } from './Utils/Debug.js';
import { dataPath } from '../Badger/Utils/DataPath.js';
import { doNothing, fail, splitList } from '@abw/badger-utils';

/**
 * Default configuration options.
 */
const defaults = {
  codec: ['yaml', 'json'],
  jsExt: ['js', 'mjs'],
};

/**
 * The Config class implements an object which can load configuration
 * files from a configuration directory.  Files can be Javascript files
 * (with `.js` or `.mjs` extensions by default) or data files using any
 * of the standard codecs (`.yaml` or `.json` by default).
 */
export class Config extends DirPath {
  /**
   * Constructor for Config object.
   * @param {String} dir - one or more directories that contain configuration files
   * @param {Object} [options] - configuration options
   * @param {Array|String} [options.jsExt='js mjs'] - Array or comma/whitespace delimited string of Javascript file extensions
   * @param {Array|String} [options.codec='yaml json'] - Array or comma/whitespace delimited string of codec names
   * @return {Object} the Config object
   */
  constructor(dir, options={}) {
    super(dir);
    const params = { ...defaults, ...options };
    this.state.codec = splitList(params.codec),
    this.state.jsExt = splitList(params.jsExt),
    addDebug(this, options.debug, options.debugPrefix, options.debugColor);
  }

  /**
   * Internal method to locate the first config file with one of a number of file extensions.
   * @param {String} uri - base part of filename
   * @param {Array} [exts] - array of possible extensions
   * @param {Function} [makeOptions] - optional function to generate options for a {@link File} object
   * @return {Object} the {@link File} object if it exists or `undefined` if not
   */
  async firstFileWithExt(uri, exts, makeOptions=doNothing) {
    const dirs = await this.dirs();

    for (let dir of dirs) {
      for (let ext of exts) {
        const path = uri + '.' + ext;
        const file = dir.file(path, makeOptions(uri, ext));
        this.debug('looking for config file: ', file.path());
        if (await file.exists()) {
          this.debug('config file exists: ', file.path());
          return file;
        }
      }
    }
    return undefined;
  }

  /**
   * Internal method to locate a Javascript configuration file with one of the `jsExt` extensions (`.js` or `.mjs` by default)
   * @param {String} uri - base part of filename
   * @return {Object} the {@link File} object if it exists or `undefined` if not
   */
  async jsFile(uri) {
    return await this.firstFileWithExt(uri, this.state.jsExt);
  }

  /**
   * Internal method to locate a configuration file with one of the `codec` extensions (`.yaml` or `.json` by default)
   * @param {String} uri - base part of filename
   * @return {Object} the {@link File} object if it exists or `undefined` if not
   */
  async file(uri) {
    return await this.firstFileWithExt(uri, this.state.codec, (uri, codec) => ({ codec }));
  }

  /**
   * Method to fetch configuration data from a file.  The file can be a Javascript file which should
   * return the configuration data as the default export, or a YAML (`.yaml`) or JSON (`.json`) file.
   * If the file isn't found then the method returns the `defaults` data if provided, or throws an
   * error if not.
   * @param {String} uri - base part of filename
   * @param {Object} [defaults] - default configuration options to be used if a file isn't found
   * @return {Object} the configuration data loaded from the file
   */
  async config(uri, defaults) {
    const [base, fragment] = uri.split('#', 2);
    let jsFile, file, data;

    // first look for a JS file, e.g. <uri>.js, <uri>.mjs
    if ((jsFile = await this.jsFile(base))) {
      data = await import(jsFile.path());
    }
    // then for a config file with a codec extension, e.g. <uri>.yaml, <uri>.yaml
    else if ((file = await this.file(base))) {
      data = await file.read();
    }
    // failing that use any default value
    else if (defaults) {
      data = defaults;
    }
    // anything else is a big pile of fail
    else {
      return fail("No configuration file for " + base);
    }
    // resolve any data path in a #fragment
    return fragment
      ? dataPath(data, fragment)
      : data;
  }
}

/**
 * Function to create a new Config object
 * @param {String} dir - directory or directories containing configuration files
 * @param {Object} [options] - configuration options
 * @param {Array|String} [options.jsExt='js mjs'] - Array or comma/whitespace delimited string of Javascript file extensions
 * @param {Array|String} [options.codec='yaml json'] - Array or comma/whitespace delimited string of codec names
 * @return {Object} the Config object
 */
export const config = (dir, options) => new Config(dir, options)

export default Config

import { splitList } from "@abw/badger-utils";
import { dir as fsDir } from "./Directory.js";

/**
 * The DirPath class implements a base class for objects that
 * can search one or more directories.
 */
export class DirPath {
  /**
   * Constructor for filesystem paths.
   * @param {String|Array} dir - comma/whitespace delimited string containing directories, or a {@link Directory} object or Array of {@link Directory} objects
   * @return {Object} the {@link DirPath} object
   */
  constructor(dir) {
    const dirs = this.initDirs(dir);
    this.state = {
      dirs
    }
  }

  /**
   * Internal method to initialise the directories.  If the `dir` argument is a string then it will be split on
   * commas and/or whitespace and converted to an array of {@link Directory} objects.  If the `dir` is a
   * {@link Directory} object then it will be wrapped in an array.  If the `dir` argument is already an array
   * (presumably of {@link Directory} objects) then no further processing is required.
   * @param {String|Array} dir - comma/whitespace delimited string containing directories, or a {@link Directory} object or Array of {@link Directory} objects
   * @return {Array} an array of {@link Directory} objects
   */
  initDirs(dir) {
    return splitList(dir).map( dir => fsDir(dir) );
  }

  /**
   * Internal method to return an array of the directories in the `dirs` argument passed to the constructor that
   * actually exist in the filesystem.  The checks to determine if the directories exists are only carried
   * out the first time the method is called.  Subsequent calls will return the cached value stored in
   * `this.state.dirsExist`.
   * @return {Array} an array of {@link Directory} objects that exist
   */
  async dirs() {
    return this.state.dirsExist
      || ( this.state.dirsExist = await this.dirsExist() );
  }

  /**
   * Internal method to determine which of the directories in the `dirs` argument passed to the constructor
   * actually exist in the filesystem.
   * @return {Array} an array of {@link Directory} objects that exist
   */
  async dirsExist() {
    const dirs = this.state.dirs;
    const exists = await Promise.all(
      dirs.map( d => d.exists() )
    );
    return dirs.filter((value, index) => exists[index]);
  }
}

export default DirPath

import process from 'node:process';
import path from 'node:path';
import Path from './Path.js'
import { file } from './File.js'
import { fail } from '@abw/badger-utils';
import { rm, mkdir, rmdir, readdir } from 'node:fs/promises'

/**
 * The Directory class implements a wrapper around a filesystem
 * directory.
 */
export class Directory extends Path {
  /**
   * Fetch a new {@link File} object for a file in the directory.
   * @param {string} path - file path
   * @param {Object} [options] - file configuration options
   * @param {String} [options.codec] - codec for encoding/decoding file data
   * @param {String} [options.encoding=utf8] - character encoding
   * @return {Object} the {@link File} object
   */
  file(path, options) {
    this.debug("file(%s, %o)", path, options);
    return file(this.relativePath(path), this.options(options));
  }

  /**
   * Fetch a new {@link Directory} object for a sub-directory in the directory.
   * @param {string} path - directory path
   * @param {Object} [options] - directory configuration options
   * @param {String} [options.codec] - default codec for encoding/decoding files
   * @param {String} [options.encoding=utf8] - default character encoding for files
   * @return {Object} the {@link Directory} object
   */
  directory(path, options) {
    this.debug("directory(%s, %o)", path, options);
    return dir(this.relativePath(path), this.options(options));
  }

  /**
   * An alias for the {@link directory} method for lazy people
   * @return {Object} the {@link Directory} object
   */
  dir(path, options) {
    this.debug("dir(%s, %o)", path, options);
    return this.directory(path, options);
  }

  /**
   * Returns a new {@link Directory} object for the parent directory
   * @param {Object} [options] - directory configuration options
   * @param {String} [options.codec] - default codec for encoding/decoding files
   * @param {String} [options.encoding=utf8] - default character encoding for files
   * @return {Object} a {@link Directory} object for the parent
   */
  parent(options) {
    this.debug("parent()");
    return this.directory('..', options);
  }

  /**
   * Returns the names of the files and sub-directories in the directory
   * @return {Promise} fulfills with an array of the file and directory names
   */
  async read() {
    this.debug("read()");
    return await readdir(this.path());
  }

  /**
   * Determines if the directory is empty.
   * @return {Promise} fulfills with a boolean value true (empty) or false (not empty).
   */
  async isEmpty() {
    this.debug("isEmpty()");
    const entries = await this.read();
    return entries.length === 0;
  }

  /**
   * Determines if the directory is not empty.
   * @return {Promise} fulfills with a boolean value true (not empty) or false (empty).
   */
  async notEmpty() {
    this.debug("notEmpty()");
    const empty = await this.isEmpty();
    return !empty;
  }

  /**
   * Empty the directory.
   * @param {Object} [options] - configuration options
   * @param {Boolean} [options.force] - force removal of files and directories
   * @param {Boolean} [options.recursive] - recursively empty and delete sub-directories
   * @return {Promise} fulfills to the {@link Directory} object
   */
  async empty(options={}) {
    this.debug("empty(%o)", options);
    if (await this.exists() && await this.notEmpty()) {
      await rm(this.path(), options);
    }
    return this;
  }

  /**
   * Make the directory.
   * @param {Object} [options] - configuration options
   * @param {Boolean} [options.recursive] - create intermediate directories
   * @return {Promise} fulfills to the {@link Directory} object
   */
  async mkdir(options={}) {
    this.debug("mkdir(%o)", options);
    const exists = await this.exists();
    if (! exists) {
      await mkdir(this.path(), options);
    }
    return this;
  }

  /**
   * Remove the directory.
   * @param {Object} [options] - configuration options
   * @param {Boolean} [options.empty] - delete items in directory
   * @param {Boolean} [options.force] - force delete files and directories
   * @param {Boolean} [options.recursive] - recursively delete sub-directories
   * @return {Promise} fulfills to the {@link Directory} object
   */
  async rmdir(options={}) {
    this.debug("rmdir(%o)", options);
    if (options.empty) {
      await this.empty(options);
    }
    if (await this.exists()) {
      await rmdir(this.path());
    }
    return this;
  }

  /**
   * Create the directory and any intermediate directories.
   * @param {Object} [options] - configuration options
   * @param {Boolean} [options.recursive=true] - recursively create intermediate directories
   * @return {Promise} fulfills to the {@link Directory} object
   */
  create(options={ recursive: true }) {
    this.debug("create(%o)", options);
    return this.mkdir(options);
  }

  /**
   * Empty and delete the directory.
   * @param {Object} [options] - configuration options
   * @param {Boolean} [options.empty=true] - empty directory of any files and sub-directories
   * @param {Boolean} [options.recursive=true] - recursively delete sub-directories
   * @param {Boolean} [options.force=true] - force deletion of files and sub-directories
   * @return {Promise} fulfills to the {@link Directory} object
   */
  destroy(options={ empty: true, recursive: true, force: true }) {
    this.debug("destroy(%o)", options);
    return this.rmdir(options);
  }

  /**
   * Assert that a directory exists and optionally create it
   * @param {Object} [options] - configuration options
   * @param {Boolean} [options.create] - create the directory and any intermediate directories if it doesn't exist - equivalent to adding `mkdir` and `recursive` options or calling {@link create}
   * @param {Boolean} [options.mkdir] - create the directory, add the `recursive` option to create intermediate directories - equivalent to calling {@link mkdir}
   * @param {Boolean} [options.recursive] - when used with `mkdir`, creates any intermediate directories
   * @return {Promise} fulfills to the {@link Directory} object
   */
  async mustExist(options={}) {
    this.debug("mustExist(%o)", options);
    if (await this.exists()) {
      return this;
    }
    if (options.mkdir) {
      return this.mkdir(options);
    }
    if (options.create) {
      return this.create();
    }
    fail("Directory does not exist: ", this.path());
  }
}

/**
 * Function to create a new {@link Directory} object
 * @param {string} path - directory path
 * @param {Object} [options] - configuration options
 * @param {Boolean} [options.codec] - a codec for encoding/decoding files
 * @return {Object} the {@link Directory} object
 */
export const dir = (path, options) => {
  return new Directory(path, options);
}

/**
 * Function to create a new {@link Directory} object for the current working directory
 * @param {Object} [options] - configuration options
 * @param {Boolean} [options.codec] - a codec for encoding/decoding files
 * @return {Object} the {@link Directory} object
 */
export const cwd = options => {
  return dir(process.cwd(), options);
}

/**
 * Function to create a new {@link Directory} object for the directory of a JS source file
 * @param {string} url - module url - from `import.meta.url`
 * @param {Object} [options] - configuration options
 * @param {Boolean} [options.codec] - a codec for encoding/decoding files
 * @return {Object} the {@link Directory} object
 */
export const bin = (url=process.argv[1], options) => {
  return dir(
    path.dirname(url.replace(/^file:\/\//, '')),
    options
  );
}

export default Directory

import path from 'node:path'
import Path from './Path.js'
import { dir } from './Directory.js'
import { codec } from '../Codecs/index.js'
import { readFile, writeFile, rm } from 'node:fs/promises'

/**
 * The File class implements a wrapper around a filesystem
 * file.
 */
export class File extends Path {
  /**
   * Returns a new {@link Directory} object for the parent directory of the file
   * @param {Object} [options] - directory configuration options
   * @param {String} [options.codec] - codec for encoding/decoding file data
   * @param {String} [options.encoding=utf8] - character encoding
   * @return {Object} a {@link Directory} object for the parent
   */
  directory(options) {
    return dir(path.dirname(this.state.path), options);
  }

  /**
   * An alias for the {@link directory} method for lazy people
   * @return {Object} the parent {@link Directory} object
   */
  dir(...args) {
    return this.directory(...args);
  }

  /**
   * Reads the file content.  If a `codec` has been specified then the content is decoded.
   * @param {Object} [options] - directory configuration options
   * @param {String} [options.codec] - codec for encoding/decoding file data
   * @param {String} [options.encoding=utf8] - character encoding
   * @return {Promise} fulfills with the file content
   * @example
   * file('myfile.txt').read().then( text => console.log(text) );
   * @example
   * file('myfile.json', { codec: 'json' }).read().( data => console.log(data) );
   * @example
   * file('myfile.json').read({ codec: 'json' }).then( data => console.log(data) );
   */
  async read(options) {
    const opts = this.options(options);
    const text = await readFile(this.state.path, opts);
    return opts.codec
      ? codec(opts.codec).decode(text)
      : text;
  }

  /**
   * Writes the file content.  If a `codec` has been specified then the content will be encoded.
   * @param {String|Object} data - directory configuration options
   * @param {Object} [options] - directory configuration options
   * @param {String} [options.codec] - codec for encoding/decoding file data
   * @param {String} [options.encoding=utf8] - character encoding
   * @return {Promise} fulfills with the file object
   * @example
   * file('myfile.txt').write('Hello World');
   * @example
   * file('myfile.json', { codec: 'json' }).write({ message: 'Hello World' });
   * @example
   * file('myfile.json').write({ message: 'Hello World' }, { codec: 'json' });
   */
  async write(data, options) {
    const opts = this.options(options);
    const text = opts.codec
      ? codec(opts.codec).encode(data)
      : data;
    await writeFile(this.state.path, text, opts);
    return this;
  }

  /**
   * Delete the file content.
   * @param {Object} [options] - directory configuration options
   * @param {Boolean} [options.force=false] - when true, exceptions will be ignored if path does not exist
   * @return {Promise} fulfills with the file object
   */
  async delete(options) {
    await rm(this.state.path, options);
    return this;
  }
}

/**
 * Function to create a new {@link File} object for a file
 * @param {String} path - file path
 * @param {Object} [options] - configuration options
 * @param {Boolean} [options.codec] - a codec for encoding/decoding files
 * @param {String} [options.encoding=utf8] - character encoding
 * @return {Object} the {@link File} object
 */
export const file = (path, options) => {
  return new File(path, options);
}

export default File

import path from 'node:path';
import { stat } from 'node:fs/promises'
import { rethrow } from '@abw/badger-utils';
import { addDebug } from '../Utils/Debug.js';

/**
 * Default configuration options.
 */
const defaults = {
  encoding: 'utf8'
}

/**
 * The Path class implements a base class for the {@link File} and {@link Directory}
 * classes.  It implements the common functionality for representing a filesystem path.
 */
export class Path {
  /**
   * Constructor for filesystem paths.
   * @param {string} path - file path
   * @param {Object} [options] - configuration options
   * @param {String} [options.encoding=utf8] - file encoding
   * @param {String} [options.codec] - codec for encoding/decoding file data
   * @return {Object} the {@link Path} object
   */
  constructor(path, options={}) {
    // allow path/file/directory to be constructed from an existing object
    if (path instanceof Path) {
      path = path.path();
    }
    this.state = { path, options: { ...defaults, ...options } };
    addDebug(this, options.debug, options.debugPrefix || 'Path', options.debugColor);
  }

  /**
   * Accessor method to return the filesystem path.
   * @return {String} the filesystem path
   */
  path() {
    return this.state.path;
  }

  /**
   * Create a path relative to the current path.
   * @param {String[]} parts - part(s) of the filesystem path
   * @return {String} the new path
   * @example
   * const p = new Path('/path/to/here')
   * const q = p.relativePath('there')          // -> /path/to/here/there
   * const r = p.relativePath('and', 'there')   // -> /path/to/here/and/there
   */
  relativePath(...parts) {
    return path.join(this.state.path, ...parts);
  }

  /**
   * Internal method to merge any options with the pre-defined options passed to the
   * constructor.  Options passed as arguments will take precedence.
   * @param {Object} options - new options
   * @return {Object} the merged options
   * @example
   * const p = new Path('/path/to/here', { option1: 'hello' })
   * const q = p.options({ option2: 'world' })  // -> { option1: 'hello', options2: 'world' }
   */
  options(options={}) {
    return { ...this.state.options, ...options };
  }

  /**
   * Method to assert that the path exists.
   * @return {Promise} fulfills with `true` if the path exists or rejects if the path doesn't
   * @example
   * const p = new Path('/path/to/here')
   * p.exists()
   *   .then( console.log('path exists') )
   *   .catch( console.log('path does not exist') )
   */
  async exists() {
    try {
      await this.stat();
      return true;
    }
    catch (error) {
      return error.code === 'ENOENT'
        ? false
        : rethrow(error);
    }
  }

  /**
   * Method to fetch stats for the path.  Uses the `stat` function from `node:fs/promises`.
   * Stats are cached internally (subject to change)
   * @return {Promise} fulfills with path stats returned from the `stat` function
   * @example
   * const p = new Path('/path/to/here')
   * p.stat()
   *   .then( stats => console.log('path stats:', stats) )
   *   .catch( console.log('path does not exist') )
   */
  async stat() {
    const stats = await stat(this.state.path);
    return this.state.stats = stats;
  }

  /**
   * Method to clear internal cache of path stats (subject to change)
   */
  unstat() {
    this.state.stats = undefined;
    return this;
  }
}

export default Path

