import { DirPath } from "./Filesystem/DirPath.js";
import { addDebug } from "./Utils/Debug.js";
import { dataPath } from "./Utils/DataPath.js";
import { fail, splitList } from "@abw/badger-utils";

/**
 * Default configuration options.
 */
const defaults = {
  jsExt: 'js mjs',
}

/**
 * The Library class implements an object which can load Javascript files
 * from one or more library directories.  Files can be Javascript files
 * (with `.js` or `.mjs` extensions by default)
 */
export class Library extends DirPath {
  /**
   * Constructor for Library object.
   * @param {String} dir - one or more directories that contain Javascript libraries
   * @param {Object} [options] - configuration options
   * @param {Array|String} [options.jsExt='js mjs'] - Array or comma/whitespace delimited string of Javascript file extensions
   * @return {Object} the Library object
   */
  constructor(dir, options={}) {
    super(dir);
    const params = { ...defaults, ...options };
    const exts = splitList(params.jsExt).map( ext => ext.replace(/^\./, '') ); // remove leading '.'
    this.state.exts = exts;
    addDebug(this, options.debug, options.debugPrefix, options.debugColor);
    this.debug("state: ", this.state)
  }

  /**
   * Method to load a Javascript library in one of the library directories and with one of the `jsExt` extensions (`.js` or `.mjs` by default).
   * Returns the exports from the library if found or throws an error if not.
   * @param {String} uri - base part of filename
   * @return {Object} the exports from the loaded libary
   */
  async library(uri) {
    const [base, fragment] = uri.split('#', 2);
    const dirs = await this.dirs();
    const exts = this.state.exts;
    for (let dir of dirs) {
      for (let ext of exts) {
        const file = dir.file(base + '.' + ext);
        this.debug('looking for module %s as', base, file.path());
        const exists = await file.exists();
        if (exists) {
          const load = await import(file.path());
          this.debug('loaded %s as', file.path());
          return fragment
            ? dataPath(load, fragment)
            : load;
        }
      }
    }
    fail("Library not found: ", uri);
  }
}

/**
 * Function to create a new Library object
 * @param {String} dir - directory or directories containing configuration files
 * @param {Object} [options] - configuration options
 * @param {Array|String} [options.jsExt='js mjs'] - Array or comma/whitespace delimited string of Javascript file extensions
 * @return {Object} the Library object
 */
export const library = (dir, options) => new Library(dir, options);

export default library;

import { isObject } from "@abw/badger-utils";

const ANSIStart  = '\u001B[';
const ANSIEnd    = 'm';
const ANSIColors = {
  reset:    0,
  bold:     1,
  bright:   1,
  dark:     2,
  black:    0,
  red:      1,
  green:    2,
  yellow:   3,
  blue:     4,
  magenta:  5,
  cyan:     6,
  grey:     7,
  white:    8,
  fg:      30,
  bg:      40,
};

/**
 * Returns an ANSI escape code for a color string.  This can be a single color
 * name, e.g. `red`, `green`, etc., or a color prefixed with `bright` or `dark`,
 * e.g. `bright red`, `dark green`, etc.  An optional section argument can be
 * set to `fg` (default) to set a foreground color or `bg` for a background color.
 * @param {String} color - color name with optional modifier prefix
 * @param {String} [base='fg'] - `fg` or `bg` to set foreground or background color respectively
 * @return {String} ANSI escape code string
 * @example
 * const str = escapeCode('red')
 * @example
 * const str = escapeCode('bright red')
 * @example
 * const str = escapeCode('bright red', 'bg')
 */
export const ANSIescapeCode = (color, base='fg') => {
  let   codes = [ ];
  let   pair  = color.split(/ /, 2);
  const hue   = pair.pop();
  const code  = (base ? ANSIColors[base] : 0) + ANSIColors[hue];
  codes.push(code);
  if (pair.length) {
    const shade = pair.length ? pair.shift() : 'dark';
    codes.push(ANSIColors[shade])
  }
  // console.log('escapeCode(%s, %s) => ', color, base, codes.join(';'));
  return ANSIStart + codes.join(';') + ANSIEnd;
}

/**
 * Returns an ANSI escape code for a color string or combination of foreground and
 * background colors.
 * @param {String|Object} colors - either a simple color name or object contain foreground and background colors
 * @param {String} [colors.fg] - foreground color
 * @param {String} [colors.fg] - background color
 * @return {String} ANSI escape code string
 * @example
 * const str = escape('red')
 * @example
 * const str = escape('bright red')
 * @example
 * const str = escape({ fg: 'bright yellow', bg: 'blue' })
 */
export const ANSIescape = (colors={}) => {
  const col = isObject(colors) ? colors : { fg: colors };
  let escapes = [ ];
  if (col.bg) {
    escapes.push(ANSIescapeCode(col.bg, 'bg'));
  }
  if (col.fg) {
    escapes.push(ANSIescapeCode(col.fg, 'fg'));
  }
  return escapes.join('');
}

/**
 * Returns an ANSI escape code to reset all colors.
 * @return {String} ANSI escape reset string
 */
export const ANSIreset = () => ANSIescapeCode('reset', false)

/**
 * Returns a function to display strings in a particular color.
 * @param {String} colors - color(s) to display string
 * @return {Function} function to display strings in the pre-defined color(s)
 */
export const color = (colors) =>
  text => ANSIescape(colors) + text + ANSIreset();

import { fail, hasValue, isArray, noValue } from "@abw/badger-utils";

/**
 * Characters that can be escaped in quoted strings.
 */
const quotedEscapes = {
  b: "\b",
  f: "\f",
  n: "\n",
  r: "\r",
  t: "\t",
  v: "\v",
  '"': '"',
  "'": "'",
  '\\': '\\',
}

/**
 * Match a double quoted string and expand escaped characters
 * @param {String} [string] - the string to match
 */
export const matchDoubleQuotedString = string => {
  const regex = /^"((?:\\[\\"nrt]|.)*?)"$/
  const match = string.match(regex);
  return match
    ? match[1].replace(/\\([\\"bfnrtv])/g, (all, one) => quotedEscapes[one] || `\\${one}`)
    : null;
}

/**
 * Match a single quoted string and expand escaped characters
 * @param {String} [string] - the string to match
 */
export const matchSingleQuotedString = string => {
  const regex = /^'((?:\\[\\']|.)*?)'$/
  const match = string.match(regex);
  return match
    ? match[1].replace(/\\([\\'bfnrtv])/g, (all, one) => quotedEscapes[one] || `\\${one}`)
    : null;
}

/**
 * Split a data path into component parts
 * @param {String} [path] - the path to split
 */
export const splitDataPath = path => {
  // * match a slash: \/
  // * match a single quoted string: '...'
  // * match a double quoted string: "..."
  // * match anything else: hello world!
  let parts = [ ];
  const regex = /(?:(\/)|'((?:\\[\\']|.)*?)'|"((?:\\[\\"nrt]|.)*?)"|([^/?]+))(\??)/g;
  const matches = [...path.matchAll(regex)];

  matches.map(
    ([ , , single, double, other, optional]) => {
      let part;
      // console.log('match [slash:%s] [single:%s] [double:%s] [other:%s]', slash, single, double, other);

      if (single) {
        part = single.replace(/\\([\\'bfnrtv])/g, (all, one) => quotedEscapes[one] || `\\${one}`);
      }
      else if (double) {
        part = double.replace(/\\([\\"bfnrtv])/g, (all, one) => quotedEscapes[one] || `\\${one}`);
      }
      else if (other) {
        part = other
      }
      if (hasValue(part)) {
        parts.push(optional ? [part, {optional:true}] : part);
      }
    }
  )
  // console.log('MATCHED ', parts);

  return parts;
}

/**
 * Traverse a data structure using a path.
 * @param {Object} [data] - the data to traverse
 * @param {String} [path] - the data path
 */
export const dataPath = (data, path) => {
  let root  = data;
  let parts = splitDataPath(path);
  let done  = [ ];
  // console.log('parts: ', parts);

  for (let part of parts) {
    const [word, opts] = isArray(part) ? part : [part, {}];
    root = root[word];
    done.push(word);
    if (noValue(root)) {
      if (opts.optional) {
        return root;
      }
      else {
        fail("No value for data at path: ", done.join('/'));
      }
    }
  }
  return root;
}

import process from "node:process"

/**
 * Returns an array of the `process.argv` array from offset 2 onwards,
 * i.e. removing the node path and script name.
 * @param {Array} argv - list of arguments, defaults to `process.argv`
 * @return {Array} array of arguments excluding the first two
 */
export function args(argv=process.argv) {
  return argv.slice(2);
}

import process from 'node:process';
import prompter from 'prompts'
import { Command } from 'commander';
import { color } from './Color.js';
import { hasValue } from '@abw/badger-utils';

export const defaults = {
  verboseColor:     'magenta',
  titleColor:       'bright yellow',
  underlineColor:   'dark yellow',
  infoColor:        'cyan',
  tickColor:        'green',
  questionColor:    'bright white',
  answerColor:      'bright green',
  sectionNewlines:  true
};

export const options = async config => {
  const vcol = color(options.verboseColor || defaults.verboseColor);
  const command = new Command;

  // set command name, description and version
  if (config.name) {
    command.name(config.name);
  }
  if (config.description) {
    command.description(config.description);
  }
  if (config.version) {
    command.version(config.version);
  }

  // define the -y / -yes and -v / --verbose options
  if (config.yes) {
    command.option('-y, --yes', 'Accept default answers')
  }
  if (config.verbose) {
    command.option('-v, --verbose', 'Verbose output')
  }
  if (config.quiet) {
    command.option('-q, --quiet', 'Quiet output')
  }

  // add in other command line options
  config.options
    .filter(
      option => {
        if (hasValue(option.arg) && ! option.arg) {
          // allow arg: false to indicate no command line argument
          return false;
        }
        if (option.title) {
          // section break
          return;
        }
        return hasValue(option.name);
      }
    )
    .map(
      option => {
        const name    = option.name;
        const about   = option.about;
        const deflt   = option.default;
        const short   = option.short;
        const type    = option.type;
        const pattern = option.pattern || (hasValue(type) ? `<${type}>` : undefined);
        let string    = `--${name}`;
        let args      = [];
        if (hasValue(short)) {
          string = `-${short}, ${string}`;
        }
        if (hasValue(pattern)) {
          string = `${string} ${pattern}`;
        }
        args.push(string);
        if (hasValue(about)) {
          args.push(about);
        }
        if (hasValue(deflt)) {
          args.push(deflt);
        }
        command.option(...args)
      }
    )

  // parse the command line arguments
  command.parse();
  const cmdline = command.opts();

  // if the -y / --yes option has been specified then accept all
  // default answers automatically
  const yes     = config.yes && cmdline.yes;
  const verbose = config.verbose && cmdline.verbose;
  const quiet   = config.quiet && cmdline.quiet;

  if (yes) {
    if (verbose && ! quiet) {
      process.stdout.write(vcol('Accepting default answers (-y option is set)\n'))
    }
    prompter.override(cmdline);
  }

  // build up the list of prompts for interactive questions
  let prompts = [ ];
  config.options.map(
    option => {
      const type     = option.type || 'text';
      const name     = option.name;
      const prompt   = option.prompt;
      const noArg    = hasValue(option.arg) && ! option.arg;
      const validate = option.validate ||
        (option.required
          ? value => (hasValue(value) && value.toString().length)
            ? true
            : (options.invalid || `You must enter a value for ${name}`)
          : undefined
        )

      // special process for initial
      // - use cmdline[name]
      // - or if noArg, use option.default
      // - if a select list and not a number, find the index
      let initial = noArg ? option.default : cmdline[name];
      if (type === 'select' && hasValue(initial)) {
        if (! Number.isInteger(initial)) {
          // console.log('looking up select option for [%s]', initial);
          initial = option.choices?.findIndex( i => i.value === initial );
          if (initial < 0) {
            initial = 0;
          }
        }
      }

      if (hasValue(prompt)) {
        prompts.push(
          {
            ...option,
            type: (
              hasValue(initial)
                ? () => {
                  yes && (quiet || answer({ question: prompt, answer: initial }));
                  return type;
                }
                : type
            ),
            name,
            message: prompt,
            initial: initial,
            validate: validate,
          },
        )
      }
      else if (option.title || option.info) {
        prompts.push(
          {
            type: () => {
              quiet || section(option);
              return false;
            }
          }
        );
      }
    }
  );

  // prompt the user to answer/confirm questions
  const answers = await prompter(prompts);

  return {
    ...cmdline, ...answers
  }
}

export const section = option => {
  const title = option.title;
  const tcol  = color(option.titleColor || defaults.titleColor);
  const ucol  = color(option.underlineColor || defaults.underlineColor);
  const icol  = color(option.infoColor || defaults.infoColor);
  const nl    = (hasValue(option.newlines) ? option.newlines : defaults.sectionNewlines) ? "\n" : "";

  if (title) {
    const uline = '-'.repeat(title.length);
    process.stdout.write(nl + tcol(title) + "\n" + ucol(uline) + "\n" + nl);
  }

  if (option.info) {
    process.stdout.write(icol(option.info) + "\n" + nl);
  }
}

export const answer = option => {
  const tcol  = color(option.tickColor || defaults.tickColor);
  const qcol  = color(option.questionColor || defaults.questionColor);
  const acol  = color(option.answerColor || defaults.answerColor);
  process.stdout.write(tcol(" ") + qcol(option.question) + " " + acol(option.answer) + "\n");
}

export default options

import { joinListOr, splitList, hasValue, fail } from "@abw/badger-utils";

/**
 * Assert that a parameter object contains an item with a defined/non-null value
 * @param {Object} params={} - parameters object
 * @param {String} name - parameter that must be included
 * @return {any} the parameter value
 * @throws {Error} if the parameter is not defined or null
 * @example
 * const foo = requiredParam({ foo: 10 }, 'foo');
 */
export function requiredParam(params={}, name) {
  const value = params[name];
  if (hasValue(value)) {
    return value;
  }
  else {
    fail("Missing value for required parameter: ", name);
  }
}

/**
 * Assert that a parameter object contains all specified item with a defined/non-null value
 * @param {Object} params={} - parameters object
 * @param {Array|String} names - parameters that must be included, as an Array or whitespace/comma delimited string (see {@link splitList})
 * @return {Array} the parameter values
 * @throws {Error} if any parameter is not defined or null
 * @example
 * const [foo, bar] = requiredParams({ foo: 10, bar: 20 }, 'foo bar');
 */
export function requiredParams(params={}, names) {
  return splitList(names).map( name => requiredParam(params, name) );
}

/**
 * An alias for {@link requiredParams} for people who don't like typing long names (and for symmetry with {@link anyParams}))
 */
export const allParams=requiredParams;

/**
 * Assert that a parameter object contains any of the specified items with a defined/non-null value
 * @param {Object} params={} - parameters object
 * @param {Array|String} names - parameters of which at least one must be included, as an Array or whitespace/comma delimited string (see {@link splitList})
 * @return {Array} the parameter values
 * @throws {Error} if any parameter is not defined or null
 * @example
 * const [foo, bar] = anyParams({ foo: 10, wiz: 99 }, 'foo bar');
 */
export function anyParams(params, names) {
  let found = false;
  const nlist  = splitList(names);
  const values = nlist.map(
    name => {
      const value = params[name];
      if (hasValue(value)) {
        found = true;
      }
      return value;
    }
  );
  return found
    ? values
    : fail("Missing value for one of: ", joinListOr(nlist));
}

import prompts from 'prompts'

/**
 * Prompt user to enter a value.
 * @param {String} question - question to prompt user to answer
 * @param {Object} [options] - optional options
 * @param {String} [options.default] - default value returned if user presses RETURN
 * @return {Promise} fulfills with response to question or default value
 * @example
 * prompt("What is your name?")
 *   .then( name => console.log(`Hello ${name}`) );
 * @example
 * prompt("What is your name?", { default: 'Mr.Badger' })
 *   .then( name => console.log(`Hello ${name}`) );
 */
export const prompt = async (question, options={}) => {
  const name = options.name || 'answer';
  const answers = await prompts([
    {
      name,
      type: options.type || 'text',
      message: question,
      initial: options.default,
    },
  ]);
  return answers[name];
}

/**
 * Prompt user to confirm a choice.
 * @param {String} question - question to prompt user to answer
 * @param {Object} [options] - optional options
 * @param {String} [options.default] - default value returned if user presses RETURN
 * @return {Promise} fulfills with response to question or default value
 * @example
 * confirm("Are you sure?")
 *   .then( yes => console.log('You said "%s"', yes ? 'YES' : 'NO') );
 */
export const confirm = async (question, options={}) =>
  prompt(question, { ...options, type: 'confirm' });

export default prompt

import { dir as fsDir } from "./Filesystem/Directory.js";
import { addDebug } from "./Utils/Debug.js";
import { Config } from "./Config.js";
import { Library } from "./Library.js";
import { fail, hasValue, splitList } from "@abw/badger-utils";

/**
 * Default configuration options.
 */
const defaults = {
  config: {
    dir: 'config',
  },
  library: {
    dir: 'lib library src components',
  }
}

/**
 * The Workspace class implements an object which acts as a central repository
 * for your project, providing access to directories, files, configuration files,
 * Javascript libraries and components.
 */
export class Workspace {
  /**
   * Constructor for Workspace object.
   * @param {String} dir - root directory for the workspace
   * @param {Object} [options] - configuration options
   * @param {Object} [options.config] - configuration options for a {@link Config} object
   * @param {Array|String} [options.config.dir] - configuration directory or directories relative to the workspace directory
   * @param {Object} [options.library] - configuration options for a {@link Config} object
   * @param {Array|String} [options.library.dir] - library directory or directories relative to the workspace directory
   * @return {Object} the Workspace object
   */
  constructor(dir, options={}) {
    const rootDir = fsDir(dir);
    const cfgDir  = splitList(options.config?.dir || defaults.config.dir).map( dir => rootDir.dir(dir) );
    const cfgOpts = { ...defaults.config, ...(options.config||{}) };
    const config  = new Config(cfgDir, cfgOpts);
    const libDirs = splitList(options.library?.dir || defaults.library.dir).map( dir => rootDir.dir(dir) );
    const libOpts = { ...defaults.library, ...(options.library||{}) };
    const library = new Library(libDirs, libOpts);

    this.state = {
      rootDir,
      config,
      library
    }

    addDebug(this, options.debug, options.debugPrefix, options.debugColor);
    this.debug('root dir: ', rootDir.path());
    this.debug('config dir: ', cfgDir.map( d => d.path() ));
    this.debug('libDirs: ', libDirs);
    this.debug('libOpts: ', libOpts);
  }

  /**
   * Fetch a new {@link Directory} object for a sub-directory of the workspace directory.
   * @param {string} path - directory path relative to the workspace directory
   * @param {Object} [options] - directory configuration options
   * @param {String} [options.codec] - codec for encoding/decoding file data
   * @return {Object} a {@link Directory} object
   */
  dir(path, options) {
    this.debug("dir(%s, %o)", path, options);
    return hasValue(path)
      ? this.state.rootDir.dir(path, options)
      : this.state.rootDir;
  }

  /**
   * Fetch a new {@link File} object for a file in the workspace.
   * @param {string} path - file path relative to the workspace directory
   * @param {Object} [options] - file configuration options
   * @param {String} [options.codec] - codec for encoding/decoding file data
   * @param {Boolean} [options.encoding=utf8] - character encoding for the file
   * @return {Object} a {@link File} object
   */
  file(path, options) {
    this.debug("file(%s, %o)", path, options);
    return this.state.rootDir.file(path, options)
  }

  /**
   * Read the content of a file in the workspace.
   * @param {string} path - file path relative to the workspace directory
   * @param {Object} [options] - directory configuration options
   * @param {Boolean} [options.codec] - codec for encoding/decoding file data
   * @param {Boolean} [options.encoding=utf8] - character encoding for the file
   * @return {Promise} fulfills with the file content
   * @example
   * file('myfile.txt').read().then( text => console.log(text) );
   * @example
   * file('myfile.json', { codec: 'json' }).read().then( data => console.log(data) );
   * @example
   * file('myfile.json').read({ codec: 'json' }).then( data => console.log(data) );
   */
  read(path, options) {
    this.debug("read(%s, %o)", path, options);
    return this.file(path, options).read();
  }

  /**
   * Writes content to a file.  If a `codec` has been specified then the content will be encoded.
   * @param {string} path - file path relative to the workspace directory
   * @param {String|Object} data - directory configuration options
   * @param {Object} [options] - directory configuration options
   * @param {Boolean} [options.codec] - codec for encoding/decoding file data
   * @param {Boolean} [options.encoding=utf8] - codec for encoding/decoding file data
   * @return {Promise} fulfills with the file object
   * @example
   * file('myfile.txt').write('Hello World');
   * @example
   * file('myfile.json', { codec: 'json' }).write({ message: 'Hello World' });
   * @example
   * file('myfile.json').write({ message: 'Hello World' }, { codec: 'json' });
   */
  write(path, data, options) {
    this.debug("write(%s, %o, %o)", path, data, options);
    return this.file(path, options).write(data);
  }

  /**
   * Fetch the configuration directory or a directory relative to it
   * @param {string} [path] - file path relative to the configuration directory
   * @param {Object} [options] - directory configuration options
   * @param {String} [options.codec] - codec for encoding/decoding data for files in the directory
   * @param {Boolean} [options.encoding=utf8] - character encoding for files in the directory
   * @return {Object} a {@link Directory} object
   */
  configDir(path, options) {
    this.debug("configDir(%s, %o)", path, options);
    return hasValue(path)
      ? this.state.configDir.dir(path, options)
      : this.state.configDir;
  }

  /**
   * Fetches configuration data from a file in the configuration directory or returns the
   * {@link Config} object itself if no file uri is specified.
   * @param {string} [uri] - file path relative to the configuration directory
   * @param {Object} [defaults] - default configuration options if file isn't found
   * @return {Promise} fulfills to the configuration data read from the file
   * @example
   * workspace.config('myfile').then(
   *   config => console.log("Loaded myfile config: ", config)
   * );
   */
  async config(uri, defaults) {
    this.debug("config(%s, %o)", uri, defaults);
    return hasValue(uri)
      ? this.state.config.config(uri, defaults)
      : this.state.config;
  }

  /**
   * Loads a Javscript library from the library directory or returns the
   * {@link Library} object itself if no file uri is specified.
   * @param {string} [uri] - file path relative to the library directory
   * @return {Promise} fulfills to the configuration data read from the file
   * @example
   * workspace.library('mylib').then(
   *   exports => console.log("Loaded mylib exports: ", exports)
   * );
   */
  async library(uri) {
    this.debug("library(%s, %o)", uri);
    return hasValue(uri)
      ? this.state.library.library(uri)
      : this.state.library;
  }

  /**
   * Loads a Javscript library from the library directory and instantiates a
   * component.
   * @param {String} uri - component base name
   * @param {Object} [props] - optional configuration properties
   * @return {Promise} fulfills to a newly instantiated component
   * @example
   * workspace.component(mycomp').then(
   *   component => console.log("Created component: ", component)
   * );
   */
  async component(uri, props) {
    const [base, fragment] = uri.split('#', 2);
    const config  = await this.config(base, {});
    const lib     = await this.library(config.component?.library || base);
    const exp     = fragment || config.component?.export || 'default';
    const compcls = lib[exp] || fail("No '", exp, "' export from component library: ", uri);
    const comp    = new compcls(this, { ...config, ...props });
    return comp;
  }
}

export const workspace = (dir, options) => new Workspace(dir, options);

export default Workspace;

